function GoSheng_SetCookie(e,t,o){let n=new Date;n.setTime(n.getTime()+24*o*3600*1e3);let i="expires="+n.toUTCString();document.cookie=e+"="+t+";"+i}function GoSheng_GetCookie(e){let t=e+"=",o=document.cookie.split(";");for(let e=o.length,n=0;n<e;n++){let e=o[n].trim();if(0==e.indexOf(t))return e.substring(t.length,e.length)}return!1}function GoSheng_DelCookie(e){let t=new Date;t.setTime(t.getTime()+-864e5);let o="expires="+t.toUTCString();document.cookie=e+"="+o}